<div class="pull-right operator-info pt5"><i class="material-icons mr-0<?php  if ($chat->fbst == 1) : ?> up-voted<?php  endif;?>">thumb_up</i><i class="material-icons<?php  if ($chat->fbst == 2) : ?> down-voted<?php  endif;?>">thumb_down</i><span class="pull-right label label-default fs12<?php  if (erLhcoreClassUser::instance()->hasAccessTo('lhchat','canchangechatstatus')) : ?> action-image<?php  endif?>" id="chat-status-text-<?php  echo $chat->id?>" <?php  if (erLhcoreClassUser::instance()->hasAccessTo('lhchat','canchangechatstatus')) : ?>title="Haga clic para cambiar estado" onclick="return lhc.revealModal({'url':WWW_DIR_JAVASCRIPT +'chat/changestatus/<?php  echo $chat->id?>'})"<?php  endif;?>><?php  if ($chat->status == erLhcoreClassModelChat::STATUS_PENDING_CHAT) : ?>Chat pendiente<?php  elseif ($chat->status == erLhcoreClassModelChat::STATUS_ACTIVE_CHAT) : ?>Chat activo<?php  elseif ($chat->status == erLhcoreClassModelChat::STATUS_CLOSED_CHAT) : ?>Chat cerrado<?php  elseif ($chat->status == erLhcoreClassModelChat::STATUS_CHATBOX_CHAT) : ?>Chatbox chat<?php  elseif ($chat->status == erLhcoreClassModelChat::STATUS_OPERATORS_CHAT) : ?>Operadores del chat<?php  endif;?></span></div><div><a onclick="return lhc.revealModal({'iframe':true,'height':350,'url':WWW_DIR_JAVASCRIPT +'chat/modifychat/<?php  echo $chat->id?>'})" title="Editar información principal del chat" href="#"><i class="material-icons mr-0">mode_edit</i></a><?php  if (!isset($hideActionBlock)) : ?><a class="material-icons mr-0" data-title="<?php  echo htmlspecialchars($chat->nick,ENT_QUOTES);?>" onclick="lhinst.startChatCloseTabNewWindow('<?php  echo $chat->id;?>',$('#tabs'),$(this).attr('data-title'))" title="Abrir en una ventana nueva">open_in_new</a><a class="material-icons mr-0" onclick="lhinst.removeDialogTab('<?php  echo $chat->id?>',$('#tabs'),true)" title="Cerrar diálogo">remove_circle_outline</a><?php  if ($chat->user_id == erLhcoreClassUser::instance()->getUserID() || erLhcoreClassUser::instan