/td><td><?php  echo htmlspecialchars($chat->department);?></td></tr><?php  endif;?><?php  if ($chat->product !== false) : ?><tr><td>Producto</td><td><?php  echo htmlspecialchars($chat->product);?></td></tr><?php  endif;?><?php  if ( !empty($chat->country_code) ) : ?><tr><td>País</td><td><img src="/chat/lhc_web/design/defaulttheme/images/flags/<?php  echo $chat->country_code?>.png" alt="<?php  echo htmlspecialchars($chat->country_name)?>" title="<?php  echo htmlspecialchars($chat->country_name)?>" /></td></tr><?php  endif;?><?php  if ( !empty($chat->user_tz_identifier) ) : ?><tr><td>Zona horaria</td><td><?php  echo htmlspecialchars($chat->user_tz_identifier)?>, <?php  echo htmlspecialchars($chat->user_tz_identifier_time)?></td></tr><?php  endif;?><?php  if ( !empty($chat->city) ) : ?><tr><td>Ciudad</td><td><?php  echo htmlspecialchars($chat->city);?></td></tr><?php  endif;?><tr><td>IP</td><td><?php  echo $chat->ip?></td></tr><?php  if (!empty($chat->referrer)) : ?><tr><td>Página</td><td><div class="page-url"><span><?php  echo $chat->referrer != '' ? '<a target="_blank" title="' . htmlspecialchars($chat->referrer) . '" href="' .htmlspecialchars($chat->referrer). '">'.htmlspecialchars($chat->referrer).'</a>' : ''?></span></div></td></tr><?php  endif;?><?php  if (!empty($chat->session_referrer)) : ?><tr><td>Vino de</td><td><div class="page-url"><span><?php  echo $chat->session_referrer != '' ? '<a target="_blank" title="' . htmlspecialchars($chat->session_referrer) . '" href="' . htmlspecialchars($chat->session_referrer) . '">'.htmlspecialchars($chat->session_referrer).'</a>' : ''?></span></div></td></tr><?php  endif;?><tr><td>ID</td><td><?php  echo $chat->id;?></td></tr><?php  if (!empty($chat->email)) : ?><tr><td>E-mail</td><td><a href="mailto:<?php  echo $chat->email?>"><?php  echo $chat->email?></a></td></tr><?php  endif;?><?php  if (!empty($chat->phone)) : ?><tr><td>Teléfono</td><td><?php  echo htmlspecialchars($chat->phone)?></td></tr><?php  endif;?><?php   ?><?ph